import React, { useState } from 'react';
import donna_logo from './images/donna_logo.png'; // Путь к изображению
import season_image_1 from './images/season.png'; // Путь к изображению
import Sidebar from './components/Sidebar';
import ProductList from './components/ProductList';
import './App.css';

function App() {
  const [cart, setCart] = useState({}); // Хранение товаров в корзине

  const [is_creating,set_creating] = useState(false)

  const handleAddToCart = (productId) => {
    setCart((prevCart) => ({
      ...prevCart,
      [productId]: (prevCart[productId] || 0) + 1, // Увеличить количество товара
    }));
  };

  const handleUpdateCart = (productId, quantity) => {
    setCart((prevCart) => {
      if (quantity <= 0) {
        const updatedCart = { ...prevCart };
        delete updatedCart[productId];
        return updatedCart;
      }
      return {
        ...prevCart,
        [productId]: quantity,
      };
    });
  };

  return (
    <div className="App">
      <div className="Header">
        <img src={donna_logo} className="logo" alt="Donna Roza" />
        <div className="season_images">
          <img src={season_image_1} className="season_image" alt="season flowers" />
          <img src={season_image_1} className="season_image" alt="season flowers" />
          <img src={season_image_1} className="season_image" alt="season flowers" />
          <img src={season_image_1} className="season_image" alt="season flowers" />
          <img src={season_image_1} className="season_image" alt="season flowers" />
        </div>
      </div>
      <div className="body">
        <Sidebar />
        <ProductList
          cart={cart}
          onAddToCart={handleAddToCart}
          onUpdateCart={handleUpdateCart}
        />
      </div>
      <div className="footer">
        <button className="button-create">Создать букет</button>
        <div className="contacts">улица, дом, телефон, инфо о компании</div>
      </div>
    </div>
  );
}

export default App;
