import React from 'react';
import './Sidebar.css'; // Стили для панели

// Массив с категориями и ссылками на изображения
const categories = [
  { name: 'Цветы', img: '/images/flowers.png' },
  { name: 'Букеты', img: '/images/bouquets.png' },
  { name: 'Шарики', img: '/images/balloons.png' },
  { name: 'Растения', img: '/images/plants.png' },
  { name: 'Игрушки', img: '/images/toys.png' },
];

function Sidebar() {
  return (
    <div className="sidebar">
      <ul>
        {categories.map((category, index) => (
          <li key={index} className="category-item">
            <img
              src={category.img}
              alt={category.name}
              className="category-image"
            />
            <span>{category.name}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Sidebar;
