// ProductList.js
import React from 'react';
import './ProductList.css'; // Добавьте стили для списка товаров

const products = [
  { name: 'Яблоки', category: 'Фрукты', price: '100 руб' },
  { name: 'Молоко', category: 'Молочные продукты', price: '60 руб' },
  { name: 'Картофель', category: 'Овощи', price: '40 руб' },
  { name: 'Вода', category: 'Напитки', price: '30 руб' },
];

function ProductList() {
  return (
    <div className="product-list">
      <h2>Список товаров</h2>
      <ul>
        {products.map((product, index) => (
          <li key={index} className="product-item">
            <p>{product.name}</p>
            <p>{product.price}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;
