import React from 'react';
import './ProductList.css'; // Стили для карточек товаров
import flower from "./images/season.png"
// Пример данных для товаров
const products = [
  { id: 1, name: 'Цветок 1', image: './images/season.png', price: '300 руб.' },
  { id: 2, name: 'Цветок 2', image: './images/flower2.jpg', price: '250 руб.' },
  { id: 3, name: 'Цветок 3', image: './images/flower3.jpg', price: '200 руб.' },
  { id: 4, name: 'Цветок 4', image: './images/flower4.jpg', price: '350 руб.' },
  { id: 5, name: 'Цветок 5', image: './images/flower5.jpg', price: '400 руб.' },
  { id: 6, name: 'Цветок 6', image: './images/flower6.jpg', price: '450 руб.' },
  { id: 7, name: 'Цветок 7', image: './images/flower7.jpg', price: '300 руб.' },
  { id: 8, name: 'Цветок 8', image: './images/flower8.jpg', price: '350 руб.' },
  { id: 9, name: 'Цветок 9', image: './images/flower9.jpg', price: '500 руб.' },
  { id: 10, name: 'Цветок 10', image: './images/flower10.jpg', price: '250 руб.' }
];

function ProductList() {
  return (
    <div className="product-list">
      {products.map((product) => (
        <div key={product.id} className="product-card">
          <img src={product.image} alt={product.name} className="product-image" />
          <div className="product-info">
            <h3 className="product-name">{product.name}</h3>
            <p className="product-price">{product.price}</p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
