import React from 'react';
import './Sidebar.css'; // Стили для панели
import flower from "C:\Users\Максим\Desktop\diplom\flower_shop\src\images\flower.png"
import bouquet from "/images/bouquet.png"
import balloons from "/images/balloons.png"
import plants from "/images/plants.png"
import toys from "/images/toys.png"

// Массив с категориями и ссылками на изображения
const categories = [
  {img: '/images/flower.png' },
  {img: '/images/bouquet.png' },
  {img: '/images/balloons.png' },
  {img: '/images/plants.png' },
  {img: '/images/toys.png' },
];

function Sidebar() {
  return (
    <div className="sidebar">
      <ul>
        {categories.map((category, index) => (
          <li key={index} className="category-item">
            <img
              src={category.img}              
              className="category-image"
            />
            <span>{category.name}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Sidebar;
